INSERT INTO tGastExamples VALUES ('ADD TO VAR', 'adding records', '~#javaj#~6   <frames> F, ~3listix command ADD TO VAR example~3~6~6   <layout of F>  EVA, 10, 10, 5, 5~6~6   ---, A     , X~6    X , tTable, -   , -   , -   , -~6      , lName, eName, lTel, eTel, bAdd~6~6#data#~6~6   <tTable>~6      name    , telephon~6~6      Rosa    ,  1111111111~6      Silvia  ,  2222222222~6      Gastona ,  6666666666~6~6#listix#~6~6   <-- bAdd>~6      VAR+, tTable, @<eName> , @<eTel>~6      MSG, tTable data!~6');
INSERT INTO tGastExamples VALUES ('ADD TO VAR', 'adding texts', '~#javaj#~6   <frames> oF, ~3listix command VAR+ example~3~6~6#listix#~6~6   <main>~6      VAR+, varBody, //This is the body of~6          ,        , //something~6          ,        , //whatsoever.~6      VAR+, varHead, //-- This is the header~6          ,        , //-- of something~6          ,        , //--~6          ,        , //~6      @<varHead>@<varBody>~6');INSERT INTO tLsxCmdAliases VALUES ('ADD TO VAR', 'ADD TO VARIABLE');
