INSERT INTO tGastItemDoc VALUES ('BODY WHILE SUB LOOP', 'listix_command', 'lang_flow', 'listix.cmds.cmdBodyWhileSubLoop', '7', 'Run or continues a loop changing some conditions', '~~6 BODY WHILE SAME applies the specified body until the given column change its value, more precisely~6 until any value of the columns until the specified one change.~6~6 A good example where to use this construction is in a header-detail report where the select is~6 suppossed to be properly sorted. Using BODY WHILE SAME command such reports can be done using just a single~6 loop, for instance a single SQL select statement.~6~6  Example:~6~6    Having the table orders (customer, date, product, quantity, price, paid)~6~6    A report with header ~3customer~3, sub-header ~3date~3 and detail ~3products~3~6    could be done with~6~6    <report>~6       LOOP, SQL,, //SELECT * FROM orders ORDER BY customer, date~6          ,, //Customer : @<customer>~6          ,, //~6          ,, BODY WHILE, customer~6          ,,           ,, //  Date : @<date>~6          ,,           ,, //~6          ,,           ,, BODY WHILE, date~6          ,,           ,,           ,, //    @<product>, @<quantity> x @<price> = @<paid>~6~6~6 An alternative way would be doing three nested loops, for example~6~6    LOOP for all customers~6        LOOP for all dates of a customer~6           LOOP for all products in a date of a customer~6~6 this would result into ~3Number of customers~3 x ~3Number of dates per customer~3 loops~6 and therefore such number of sql queries as well.~6~6');
INSERT INTO tGastExamples VALUES ('BODY WHILE SUB LOOP', 'body while agenda sample', '~#javaj#~6~6   <frames> oConsole, ~3Sample RUN LOOP~3~6~6#data#~6~6   <telephones>~6      NAME  , KIND   , NUMBER~6      peter , private, 111~6      peter , private, 222~6      peter , private, 333~6      peter , work   , 444~6      paul  , work   , 555~6      mary  , private, 666~6      mary  , private, 777~6      mary  , work   , 888~6      mary  , work   , 999~6~6#listix#~6~6   <main0>~6      //I have the telephone numbers of @<show people>~6      //~6      //Hier is my book:~6      //~6      @<show agenda>~6~6   <show people>~6      LOOP, EVA, telephones~6          , DIFFERENT HEADER, NAME~6          , LINK, ~3, ~3~6          ,, @<NAME>~6~6   <show agenda>~6      LOOP, EVA, telephones~6          ,, //Telephones of @<NAME> :~6          ,, //~6          ,, WHILE, NAME~6          ,,      ,, // @<KIND> : ( @<listOfPart> )~6          ,, //~6          ,, //-----------------------~6~6   <listOfPart>~6        WHILE, KIND~6             , LINK, ~3, ~3~6             ,, //@<NUMBER>');
INSERT INTO tGastExamples VALUES ('BODY WHILE SUB LOOP', 'orders detail sample', '~#javaj#~6~6   <frames> oConsole, ~3Sample BODY WHILE SAME~3~6~6#data#~6~6   <orders>~6      customer , date       , product   , quantity, price~6      suelen   , 2016-03-01 , ruedas    , 1430    , 6320~6      suelen   , 2016-03-01 , tornillos ,   24    ,   53~6      suelen   , 2016-03-01 , cables    ,  110    ,   80~6      pocoyo   , 2016-03-01 , ruedas    , 1430    , 6320~6      pocoyo   , 2016-03-01 , ruedas    , 1430    , 6320~6      pocoyo   , 2016-03-01 , ruedas    , 1430    , 6320~6      suelen   , 2016-03-25 , ruedas    , 1430    , 6320~6      suelen   , 2016-03-25 , tornillos ,  470    , 6326~6      comealgo , 2016-07-16 , ruedas    ,    2    , 6500~6      comealgo , 2016-07-16 , bandejas  ,   55    ,   43~6      comealgo , 2016-07-16 , yogures   ,  130    ,    3~6~6#listix#~6~6   <main0> LSX, report~6~6   <report>~6       LOOP, VAR, orders~6          ,, //Customer : @<customer>~6          ,, //~6          ,, BODY WHILE, customer~6          ,,           ,, //  Date : @<date>~6          ,,           ,, //~6          ,,           ,, BODY WHILE, date~6          ,,           ,,           ,, //    @<product>, @<quantity> x @<price>~6');INSERT INTO tLsxCmdAliases VALUES ('BODY WHILE SUB LOOP', 'BODY WHILE SAME');
INSERT INTO tLsxCmdAliases VALUES ('BODY WHILE SUB LOOP', 'BODY WHILE');
INSERT INTO tLsxCmdAliases VALUES ('BODY WHILE SUB LOOP', 'WHILE');
INSERT INTO tLsxCmdAliases VALUES ('BODY WHILE SUB LOOP', 'SUB LOOP WHILE');
INSERT INTO tLsxCmdAliases VALUES ('BODY WHILE SUB LOOP', 'SUB LOOP WHILE SAME');INSERT INTO tlsxCmdSyntaxHead VALUES ('BODY WHILE SUB LOOP', '1', '8', 'Performs a sub-loop within the current loop (LOOP TABLE)');INSERT INTO tlsxCmdSyntaxParams VALUES ('BODY WHILE SUB LOOP', '1', '0', 'fieldName', '', 'use the provided sub-body while the value of the record from the first field until fieldName remains the same');INSERT INTO tlsxCmdSyntaxOpts VALUES ('BODY WHILE SUB LOOP', '1', 'LINK', 'text4LinkRows', '', 'Text to be used as link between each two records in the loop. If the option is not specified then the native return is used as link string');
INSERT INTO tlsxCmdSyntaxOpts VALUES ('BODY WHILE SUB LOOP', '1', 'FILTER', 'fieldName, operator, value', '', 'Skip records that does not fit the given condition for the field. If more filter given, skip those record that does not fit any of the filters (OR join)');
INSERT INTO tlsxCmdSyntaxOpts VALUES ('BODY WHILE SUB LOOP', '1', 'BODY', 'subcommand', '', '~Executed on each iteration, note that this is the default option thus the word ~3BODY~3 can be omited');
INSERT INTO tlsxCmdSyntaxOpts VALUES ('BODY WHILE SUB LOOP', '1', 'HEAD', 'subcommand', '', 'Executed (if rows >= 1) before the body execution of the first row. On executing HEAD loop column variables are set to the first row');
INSERT INTO tlsxCmdSyntaxOpts VALUES ('BODY WHILE SUB LOOP', '1', 'TAIL', 'subcommand', '', 'Executed (if rows >= 1) after the body execution of the last row. On executing TAIL there are not loop column variables set');
INSERT INTO tlsxCmdSyntaxOpts VALUES ('BODY WHILE SUB LOOP', '1', 'IF NO RECORD', 'subcommand', '', 'Executed if the table to loop result in 0 rows that is the table is empty.');
