INSERT INTO tGastExamples VALUES ('HTTP DISTILL', 'Http DISTIL example', '~#javaj#~6~6   <frames>~6       oConsole, ~3Http DISTIL example~3, 200, 300~6~6#listix#~6~6   <main>~6       VAR=, fres1, ~3:(mem) response1~3~6       VAR=, fres2, ~3:(mem) response2~3~6       GEN, @<fres1>, htt response data1~6       GEN, @<fres2>, htt response data chunked~6       //==== STATUS response 1:[~6       HTTP DISTILL, @<fres1>, STATUS MSG~6       //]~6       //==== BODY response 1:[~6       HTTP DISTILL, @<fres1>, BODY~6       //]~6       //==== HEADER response 1:[~6       HTTP DISTILL, @<fres1>, HEADER~6       //]~6       //==== STATUS CODE response 2:[~6       HTTP DISTILL, @<fres2>, STATUS CODE~6       //]~6       //==== HEADER Conten-Type response 2:[~6       HTTP DISTILL, @<fres2>, HEADER, ~3Content-Type~3~6       //]~6       //==== HEADER MiVar response 2:[~6       HTTP DISTILL, @<fres2>, HEADER, ~3MiVar~3, ~3?~3~6       //]~6       //==== BODY response 2:[~6       HTTP DISTILL, @<fres2>, BODY~6       //]~6~6   <htt response data1>~6      //HTTP/1.1 200 OK~6      //Date: Thu, 27 Dec 2012 21:06:13 GMT~6      //Server: Apache~6      //Content-Type: application/json~6      //Content-Language: es~6      //~6      //This data is not chunked~6      //second line~6      //third etc~6      //end of message~6      //~6~6   <htt response data chunked>~6      //HTTP/1.1 200 OK~6      //Date: Thu, 27 Dec 2012 21:06:13 GMT~6      //Server: Apache~6      //Transfer-Encoding: chunked~6      //Content-Type: application/json~6      //Content-Language: es~6      //~6      //A~6      //1234567890~6      //10~6      //long text~6      //abcde~6      //3~6      //fin~6      //~6');INSERT INTO tLsxCmdAliases VALUES ('HTTP DISTILL', 'HTTP DISTIL');
