INSERT INTO tGastItemDoc VALUES ('IN CASE', 'listix_command', 'lang_flow', 'listix.cmds.cmdInCase', '7', 'For conditional execution of listix commands', '~~6 For conditional execution of commands (short alias IF). Compares the given ~4mainValue~4~6 with all ~4caseValues~4 and execute the given ~4subCommand~4 when the comparation matches.~6 Note that this command uses ~3string comparation~3 which may be not suitable for numberic~6 values, for numeric comparation use the command ~3IN CASE NUM~3.~6 If ~4subCommand~4 contains just one column it behaves as an inline format of just one line.~6~6 Examples:~6~6       IF, @<myVar>, =, ~3TRUE~3, SETVAR, myText, ~3Verified myVar is truly true~3~6       IF, @<myVar>, <>, ~3TRUE~3, //Verified myVar is truly not true~3~6~6       IN CASE, @<myVar>,~6              , 0       , //false~6              , 1       , //true~6              , TRUE    , //true~6              , FALSE   , //false~6              , ELSE    , BOX, W, ~3Why your myVar is not boolean?~3~6');
INSERT INTO tGastExamples VALUES ('IN CASE', 'switch of one variable', '~#javaj#~6~6   <frames> F, ~3listix command example~3~6~6   <layout of F>~6     EVA, 10, 10, 5, 5~6~6     ---,            ,  40   , 200~6        , lMy Boolean, eMyVar, eMyVarText~6        , lEnter a value and press enter key, -, -~6~6#listix#~6~6    <-- eMyVar>~6         IN CASE, @<eMyVar>~6                , 0   ,  SET VAR, eMyVarText, //FALSE, LIE, NOT~6                , 1   ,  SET VAR, eMyVarText, //TRUE, REAL, YES~6                , ELSE,  SET VAR, eMyVarText, //UNKNOWN, MAYBE, JAIN~6         MSG, eMyVarText data!');
INSERT INTO tGastExamples VALUES ('IN CASE', 'evaluating arguments', '~#javaj#~6~6   <frames> oConsole, ~3arguments evaluation sample~3~6~6#listix#~6~6  <main0>~6     LISTIX, my procedure, my first argument, yet another, third one, etc~6~6  <my procedure>~6    IN CASE, @<:lsx paramCount>~6           , 0   , //No arguments passed!~6           , 1   , //Just one argument~6           , 2   , //Only two arguments~6           , 3   , //There are three arguments~6           , ELSE, //There are lots of arguments~6    //~6    //passed (@<:lsx paramCount>) arguments :~6    IN CASE, @<:lsx paramCount>, ~3>~3~6           , 0, // ~3@<p1>~3~6           , 1, // , ~3@<p2>~3~6           , 2, // , ~3@<p3>~3~6           , 3, // , there are more arguments ...~6~6');
INSERT INTO tGastExamples VALUES ('IN CASE', 'detecting options', '~#javaj#~6~6   <frames> F, ~3listix command example~3, 500, 400~6~6   <layout of F>~6      EVA, 10, 10, 5, 5~6~6      ---, X~6         , kOption1~6         , kOption2~6      X  , oConsole~6~6#listix#~6~6~6  <-- kOption1> @<describe>~6  <-- kOption2> @<describe>~6~6  <describe>~6       //~6       //Current selection~6       //~6       IN CASE , 1~6               , @<kOption1 selected>, // Option 1 is checked~6               , @<kOption2 selected>, // Option 2 is checked~6               , ELSE                , // No option is ckecked');INSERT INTO tLsxCmdAliases VALUES ('IN CASE', 'IF');
