INSERT INTO tGastItemDoc VALUES ('IN CASE NUM', 'listix_command', 'lang_flow', 'listix.cmds.cmdInCaseNumeric', '7', 'For conditional execution of listix commands based on numeric values', '~~6 For conditional execution of commands (short alias IF NUM). Compares the given ~4mainValue~4~6 with all ~4caseValues~4 and execute the given ~4subCommand~4 when the comparation matches.~6 If ~4subCommand~4 contains just one column it behaves as an inline format of just one line.~6~6 Examples:~6~6       IF NUM, myVar-1, =, 0, SETVAR, myText, ~3Verified myVar is truly true~3~6       IF NUM, myVar+1, >, 1, //Verified myVar is truly not true~3~6~6       IN CASE NUM, myVar * yourVar + hisVar,~6                  , 0       , //false~6                  , 1       , //true~6                  , ELSE    , BOX, W, ~3Why your myVar * yourVar + hisVar is not boolean?~3~6');
