INSERT INTO tlsxCmdSyntaxOpts VALUES ('IN FILE', '1', 'AS TEXT', '0 / 1', '0', 'Default is 0, setting to 1 has the same effect as set SOLVE VAR to 0');
INSERT INTO tGastItemDoc VALUES ('JAVA STATIC', 'listix_command', 'system_run', 'listix.cmds.cmdJavaMain', '3', 'To call a java method', '~~6  Calls a java static method within the same java virtual machine where Listix is running.~6  Through this command it is possible to customize a lot of functionality, from fast calculations~6  to long processes, the only limitation is the input parameters (void or String[]).~6~6  The method to call has to have one of the following signatures~6~6   1  public static String methodname ()~6   2  public static String methodname (String [])~6   3  public static String [] methodname ()~6   4  public static String [] methodname (String [])~6   5  public static anotherType methodname ()~6   6  public static anotherType methodname (String [])~6~6  Use the option ~3ARG TYPE, void~3 if the method has no arguments (1, 3, 5)~6~6  If the method return~4s type is different from String or String [] (e.g. 5, 6) the return value will be ignored~6  without producing any error.~6~6  In the cases 1 to 4 the returned String(s) will be sent to the current listix~6  target (e.g. console, variable solving etc). Note though the difference between returning a String~6  or String[] and just printing out values using System.out within the method, for example~6~6       ...~6       SETVAR, myVar, @<call method>~6~6    <call method> JAVA, class, method~6~6  The result will only be set into myVar if the method returns it, but nothing that the method~6  simply prints out (e.g. using System.out.print) will be contained in myVar.~6~6  Like in the command CALL, this command waits for the termination of the execution before~6  continue.~6~6  If the method to call needs extra libraries or classes these should be loaded previously~6  using either the Javaj variable <external_libraries> or the command ~3EXTERNAL, PATH~3.~6~6 (*) Strings can be also texts, for return value as much as for input arguments, for example~6~6    JAVA, myclass, myMethod, @<some Text>, arg2, etc~6');
INSERT INTO tGastExamples VALUES ('JAVA STATIC', 'calling java methods', '~#javaj#~6~6   <frames> F, ~3calling a java method~3~6~6   <layout of F>~6~6      EVA, 10, 10, 5, 5~6      ---,    X~6         , bTestCall~6       X , oConsola~6~6#listix#~6~6   <-- bTestCall>~6       //Calling listix.util.lsx2Html, main ...~6       JAVA STATIC, listix.util.lsx2Html, main~6       //~6       //Calling listix.lsxWriter main ...~6       JAVA STATIC, listix.lsxWriter~6       //~6       //Calling the method de.elxala.langutil.filedir.naming::toVariableName~6       // to convert ~3This is~820~8NOT(a valid \\//) file name, is it?~3~6       // into a valid variable name (e.g. for a column in a table etc)~6       //~6       JAVA, de.elxala.langutil.filedir.naming, toVariableName, //This is~820~8NOT a valid () file name, is it?~6       //~6       //Note : the last result can be obtained using the command STRCONV, TEXT-VARNAME~6       //');
INSERT INTO tGastExamples VALUES ('JAVA STATIC', 'testing math expresion', '~#javaj#~6~6   <frames> F, ~3testing math expresion algebraic <-> RPN~3, 600, 400~6~6   <layout of F>~6~6      EVA, 10, 10, 5, 5~6      ---,    , X~6         , lExp. Algebraica, eAljbr~6         , lExp. Polaca    , ePolaca~6       X , oConsola, -~6#data#~6~6~7<eAljbr>  //sin (x) / x~6~7<ePolaca> //x 3 - 6 / cos~6~6#listix#~6~6   <-- eAljbr>~6~7    JAVA STATIC, de.elxala.math.polac.aljbr2polaca, main, @<eAljbr>~6~6   <-- ePolaca>~6~7    JAVA STATIC, de.elxala.math.polac.polaca2aljbr, main, @<ePolaca>');
INSERT INTO tGastExamples VALUES ('JAVA STATIC', 'Asiste expression sample1', '~#javaj#~6~6   <frames> F, ~3Asiste string utility~3, 600, 400~6~6   <layout of F>~6~6      EVA, 10, 10, 5, 5~6      ---,    , X~6         , lAsiste expression, eAsisteStr, bGo~6       X , xSQLFilter, -, -~6~6   <sysDefaultFonts>~6      Consolas, 13, 0, TextArea~6~6#data#~6~6   <eAsisteStr> +generation +GUI -XML~6~6   <xSQLFilter wrapLines> 1~6~6#listix#~6~6   <-- bGo>~6      -->, xSQLFilter data!,, @<solve>~6~6   <-- eAsisteStr>~6      -->, xSQLFilter data!,, @<solve>~6~6   <solve>~6      JAVA STATIC, javaj.widgets.table.util.utilAsiste, getComposedWhereCondition, @<eAsisteStr>, desc~6');
INSERT INTO tGastExamples VALUES ('JAVA STATIC', 'Asiste expression sample2', '~#javaj#~6~6   <frames> F, ~3Asiste string utility for more fields~3, 600, 400~6~6   <layout of F>~6~6      EVA, 10, 10, 5, 5~6      ---,    , X~6         , lAsiste expression, eAsisteStr, allany~6       X , xSQLFilter, -, -~6~6   <layout of allany>~6      RADIO, X~6~6      rAny, rAll~6~6   <sysDefaultFonts>~6      Consolas, 13, 0, TextArea~6~6#data#~6~6   <rAny selected> 1~6   <eAsisteStr> +generation +GUI -XML~6~6   <xSQLFilter wrapLines> 1~6~6#listix#~6~6   <-- allany>~6      -->, xSQLFilter data!,, @<solve>~6~6   <-- eAsisteStr>~6      -->, xSQLFilter data!,, @<solve>~6~6   <solve>~6      JAVA STATIC, javaj.widgets.table.util.utilAsiste, @<method>, @<eAsisteStr>, desc, help~6~6~6   <method>~6      CHECK, VAR, rAny selected, //getComposedWhereCondition~6      CHECK, ==, @<rAny selected>, 1, //getComposedWhereConditionAll~6      getComposedWhereConditionAny~6');INSERT INTO tLsxCmdAliases VALUES ('JAVA STATIC', 'JAVA MAIN');
