INSERT INTO tGastItemDoc VALUES ('LAUNCH', 'listix_command', 'system_run', 'listix.cmds.cmdLaunch', '3', 'To launch an external program', '~~6  Launches an external program or system command using the standard output streams (stdout stderr)~6  whithout waiting for its finalization (see also command CALL).~6  This kind of call is useful, for instance, to launch an external editor or html browser~6  or whenever a process should not block the flow of Listix.~6~6  WARNING: Avoid, or be very careful, calling critical system commands like file deletion etc.~6           and, in general, call system commands or other executables always with the proper~6           knowledge about them. Specially in this case it is strongly recomendable NOT to~6           practice the try-error approach.~6~6  ---- Notes for windows ----~6      (the same as in command CALL)~6~6  ---- General Troubleshooting ----~6      (the same as in command CALL)~6');
INSERT INTO tGastExamples VALUES ('LAUNCH', 'launching something', '~#javaj#~6~6   <frames> F, ~3launching explorer~3~6~6   <layout of F>~6~6      PANEL, X~6      bTestCall~6~6#listix#~6~6   <-- bTestCall>~6       CHECK, LINUX, LAUNCH, //explorer.exe /n,/e,/select,.~6       LAUNCH, //vi~6');INSERT INTO tLsxCmdAliases VALUES ('LAUNCH', 'LANZA');INSERT INTO tlsxCmdSyntaxHead VALUES ('LAUNCH', '1', '3', '~Launches (execute and do not wait) a command through Runtime~4s exec method using defaults output streams');INSERT INTO tlsxCmdSyntaxParams VALUES ('LAUNCH', '1', '0', 'command', '', 'program to call');
INSERT INTO tlsxCmdSyntaxParams VALUES ('LAUNCH', '1', '1', 'parameter', ' ', 'first parameter of the program');
INSERT INTO tlsxCmdSyntaxParams VALUES ('LAUNCH', '1', '2', '...', ' ', 'further parameters');INSERT INTO tlsxCmdSyntaxOpts VALUES ('LAUNCH', '1', 'ON BATCH', '1 / 0', '0', 'If true (1) executes the command using a batch file (or script in linux). Note in Windows it might be necessary in some special cases due to a bug in the java function Runtime.getRuntime().exec (I guess)');
INSERT INTO tlsxCmdSyntaxOpts VALUES ('LAUNCH', '1', 'VERBOSE', '0 / 1/ 2', '0', '~If 1 prints out the command at the beginning (~3CALL [command]~3)');
INSERT INTO tGastItemDoc VALUES ('LAUNCH FILE', 'listix_command', 'system_run', 'listix.cmds.cmdLaunchOpenFile', '3', 'Launches the associated application and opens the given file', '~~6  Launches the default application associated with the given url or file. This command tries to be~6  OS independent. For windows calls directly the command ~3start~3 and for not windows (e.g. linux)~6  calls ~4xdg-open~4. If another call is desired use instead the command LAUNCH.~6');
INSERT INTO tGastExamples VALUES ('LAUNCH FILE', 'browse local', '~#javaj#~6~6   <frames> oSal~6~6#listix#~6~6   <helloContent>~6       //<html>Hello!</html>~6~6   <main>~6      VAR=, tmphtml, @<:lsx tmp html>~6      GEN, @<tmphtml>, helloContent~6      //generated temporary html file @<tmphtml>~6      //~6      BROWSER, @<tmphtml>~6      //browser launched.~6~6');
INSERT INTO tGastExamples VALUES ('LAUNCH FILE', 'browse calcRoute', '~#javaj#~6~6   <frames>~6      main, To google maps, 300~6~6   <layout of main>~6      EVA, 4, 4, 2, 2~6~6         ,             , X~6         , lOrigin     , eOrigin       , bOriginToMap~6         , lDestination, eDestination  , bDestToMap~6         , bRoute~6~6#data#~6~6   <eOrigin>         //48.777641,9.164349~6   <eDestination>    //48.742323,9.309448~6~6   <bOriginToMap> to Map~6   <bDestToMap> to Map~6~6#listix#~6~6   <-- bOriginToMap> LSX, MAPS1, @<eOrigin>~6   <-- bDestToMap>  LSX, MAPS1, @<eDestination>~6   <-- bRoute>~6      LSX, MAPS2, @<eOrigin>, @<eDestination>~6~6   <MAPS1>~6      BROWSER, http://maps.google.de/maps?q=@<p1>&t=m&z=19~6~6   <MAPS2>~6      BROWSER, http://maps.google.de/maps?saddr=@<p1>&daddr=@<p2>&t=m');INSERT INTO tLsxCmdAliases VALUES ('LAUNCH FILE', 'OPEN');
