INSERT INTO tGastItemDoc VALUES ('PARSER EVA', 'listix_command', 'data_parsers', 'listix.cmds.cmdParserEva', '4', 'Parses an EVA file and stores the data into a database', '~~6 Parses an EVA file and stores the file name and the eva structure into a database.~6');
