INSERT INTO tlsxCmdSyntaxParams VALUES ('PARSONS', '3', '8', 'targetDbName', '(default db)', 'Database name where to put the result of parsing if some agent DBTABLE is given');INSERT INTO tlsxCmdSyntaxOpts VALUES ('PARSONS', 'x', 'DBTABLE', 'tableName, [CONST, constColumns, constValues]', '', 'Start of a parse agent (pattern(s) has to follow) that will store results as records in the given tableName of the database given as command parameter. Optionally a set of constants and its values might be given, constColumns is a string with all fields comma separated (might be enclosed in quotes) and constValues a SQL expression of the values also comma separated if more than one field is given.');
