INSERT INTO tGastItemDoc VALUES ('RESUTIL', 'listix_command', 'system_files', 'listix.cmds.cmdResourceUtil', '3', 'Utilities for resources, urls and micro-tools', '~~6 Utilities for checking and copying resources and the named Gastona micro-tools.~6 Meaning by resource that file that can be found within the current java class path,~6 typically read only, for example a file found into a jar file.~6 A Gastona micro-tool is a resource file that will be extracted automatically~6 when needed acording with some rules defined in the resource file~6 META-GASTONA/muTools/muToolsManifest.eva.~6~6 Gastona has at least one micro-tool which is sqlite (sqlite.exe or sqlite.bin for linux) and~6 it will be extracted onto a temporary directory when required internally either by Listix~6 commands or by Javaj zWidgets related with database operations.~6');
INSERT INTO tGastExamples VALUES ('RESUTIL', 'example resource', '~#javaj#~6~6   <frames> F, ~3example listix RESOURCE~3~6~6   <layout of F>~6~6   EVA, 10, 10, 5, 5~6   ---,           ,    X      ,~6      , lResource , eResource , bCheckResource~6      , lMicroTool, eMicroTool, bCheckMicroTool~6      , lUrl      , eUrl      , bCheckUrl~6    X , oConsola  , -         , -~6~6#data#~6~6    <eResource>  javaj/img/ok.png~6    <eMicroTool> sqlite~6    <eUrl>       http://www.gastona.org/index.html~6~6    <bCheckResource> Check~6    <bCheckMicroTool> Get muTool~6    <bCheckUrl> Check~6~6#listix#~6~6   <-- bCheckResource>~6       //Check resource ~3@<eResource>~3 =~6       RESOURCE, EXISTS, @<eResource>~6       //~6       //~6~6   <-- bCheckMicroTool>~6       //Micro tool ~3@<eMicroTool>~3 = [~6       RESOURCE, MICRO TOOL, @<eMicroTool>~6       //]~6       //~6~6   <-- bCheckUrl>~6       //Check Url ~3@<eUrl>~3 =~6       RESOURCE, EXISTS URL, @<eUrl>~6       //~6       //~6');INSERT INTO tLsxCmdAliases VALUES ('RESUTIL', 'RESOURCE');
