INSERT INTO tGastItemDoc VALUES ('SCAN', 'listix_command', 'system_files', 'listix.cmds.cmdScanFiles', '5', 'For scan directories or zipp files into a database', '~~6 Scan a directory or a zip (or jar) file and set the result into a database.~6 Having a directory into a database is convenient for searching and filtering~6 the files in an arbitrary way using sql selects.~6 If the database does not exists then it is created with the following schema~6~6       table (scan)_roots : rootID, rootLabel, pathRoot, rootType, timeLastScan~6       table (scan)_files : rootID, fileID, pathFile, fileName, extension, date, size~6       view  (scan)_all   : which is a join of the two tables plus the formed fields fullPath,~6                            fullParentPath and subPath~6~6 Example of entry in the database:~6~6       Scaning the directory c:\myDir gives just one file under subdir\text.txt, then the~6       fields for the table scan_roots might be~6~6          rootID        1~6          rootLabel    ~3local~3~6          pathRoot     ~3c:\myDir~3~6          rootType     ~3D~3~6          timeLastScan ~321-06-2009 21:07~3~6~6       and for the table scan_files~6~6          rootID      1~6          fileID      1~6          pathFile    ~3subdir~3~6          fileName    ~3text.txt~3~6          extension   ~3txt~3~6          date        ~328-03-2003 09:02~3~6          size        182887~6~6       additionaly to all these fields, in the view scan_all we found as well~6~6          fullPath         ~3c:\myDir\subdir\text.txt~3~6          fullParentPath   ~3c:\myDir\subdir~3~6          subpath          ~3subdir\text.txt~3~6~6 Pre-filtering the scan~6~6    For big directories or just because we are only interesting in some kind of files the~6    entries in the database might be reduced using multiple pairs of : optFilter, regexp~6~6    optFilter might be~6~6      +E  the extension matching ~4regexp~4 will be included (excluding all those not included)~6      -E  the extension matching ~4regexp~4 will be excluded (including all those not excluded)~6      +D  the relative directory matching ~4regexp~4 will be included (excluding all ...)~6      -D  the relative directory matching ~4regexp~4 will be excluded (including all ...)~6      +F  the file matching ~4regexp~4 will be included (excluding all ...)~6      -F  the file matching ~4regexp~4 will be excluded (including all ...)~6~6      Java Regular expresions are accepted as ~4textFilter~4~6         Examples:~6~6            -E, ~3obj~3      exclude extensions obj~6            -F, ~3\.obj$~3    exclude extensions obj~6            -D, ~3lint~3     exclude the directory ~3/lint/~3 and its subdirectories~6            +F, ~3Proxy~3,   add the files which name includes ~3Proxy~3~6');
INSERT INTO tGastExamples VALUES ('SCAN', 'scan sample', '~#javaj#~6~6   <frames> oConsole, SCAN sample, 600, 400~6~6#listix#~6~6   <main>~6      //Scanning ~3.~3 ..~6      SCAN, ADD FILES,, .~6      //done.~6      //~6      //LIST OF FIRST 10 FILES:~6      //~6      LOOP, SQL,, //SELECT fullPath FROM scan_all LIMIT 10;~6          ,, // @<fullPath>~6      //~6      //MAIN EXTENSIONS:~6      //~6      LOOP, SQL,, //SELECT COUNT(*) AS nfiles, extension FROM scan_files GROUP BY extension ORDER BY nfiles DESC LIMIT 10;~6          ,, // extension [@<extension>] in @<nfiles> file(s)~6');
INSERT INTO tGastExamples VALUES ('SCAN', 'scan sample 2', '~#javaj#~6~6   <frames> Fmain, SCAN sample, 600, 400~6~6   <layout of Fmain>~6      EVA, 10, 10, 7, 7~6~6         ,           , X~6         , lDirectory, eDir, bSearch dir, gLus~6       X , sFileViewer, -, -, -~6~6#data#~6~6   <bSearch dir DIALOG> DIR~6   <bSearch dir image> javaj/img/explorer.png~6~6#listix#~6~6   <-- bSearch dir>~6      -->, eDir data!,, @<bSearch dir chosen>~6      @<do scanDir>~6~6   <-- eDir>~6      @<do scanDir>~6~6   <do scanDir>~6      SCAN, ADD FILES,, @<eDir>~6       -->, sFileViewer data!, sqlSelect, //@<select this dir>~6~6   <select this dir>~6      //SELECT pathFile, fileName, extension, date, size~6      //  FROM scan_all~6      //  WHERE pathRoot = ~4@<eDir>~4~6      // ;');
INSERT INTO tGastExamples VALUES ('SCAN', 'look at scan schema', '~#javaj#~6~6   <frames> oConsola, Look At DB schema of SCAN~6~6#listix#~6~6   <main0>~6      SCAN, ADD FILES,, .~6~6      GEN,, print schema (db), listix, META-GASTONA/utilApp/std/printSchema.lsx~6         , PARAMS, ~3~3~6      GEN,, lookAtDB (db), listix, META-GASTONA/utilApp/std/lookAtDB.lsx~6         , PARAMS, ~3~3');INSERT INTO tLsxCmdAliases VALUES ('SCAN', 'SCAN');
