INSERT INTO tGastItemDoc VALUES ('SET VAR', 'listix_command', 'lang_variables', 'listix.cmds.cmdSetVariable', '7', 'Sets a value into an Eva variable in the unit #data#', '~~6 Sets a value into an Eva variable in the unit #data#. If the variable does not exist yet it will~6 be created and if the variable already had contents it will be replaced. After executing the command~6 the variable will have just one row, although it might have more columns. To add new rows use the~6 command ADD TO VAR.');
INSERT INTO tGastExamples VALUES ('SET VAR', 'sample setvar 1', '~#javaj#~6~6   <frames> oConsole, ~3SET VAR sample~3~6~6#data#~6~6    <myOldVar>~6       the default value contains~6       several columns, and~6       also, several, rows~6~6#listix#~6~6    <dumpVariables>~6       //~6       //=== variables @<p1>~6       //~6       DUMP, data,, myOldVar, myNewVar~6~6   <main0>~6       LISTIX, dumpVariables, //at the beginning~6       SET VAR, myNewVar, simple~6       LISTIX, dumpVariables, //after ~3SET VAR, myNewVar, simple~3~6       SET VAR, myNewVar, more, complex, value, //one,two,three~6       LISTIX, dumpVariables, //after SET VAR, myNewVar, more, complex,...~6       SET VAR, myOldVar, end of sample~6       LISTIX, dumpVariables, //after ~3SET VAR, myOldVar, end of sample~3');
INSERT INTO tGastExamples VALUES ('SET VAR', 'setting an edit field', '~#javaj#~6   <frames> F, ~3listix command SET VAR example~3~6~6   <layout of F>  EVA, 10, 10, 5, 5~6~6   ---, A~6    X , iList~6      , eCampo~6~6#data#~6   <iList>~6      name~6~6      Rosa~6      Silvia~6      Gastona~6~6#listix#~6~6   <-- iList>~6      SET VAR, eCampo, @<iList selected.name>~6      MSG, eCampo data!~6');INSERT INTO tLsxCmdAliases VALUES ('SET VAR', 'SET VARIABLE');
