INSERT INTO tGastExamples VALUES ('SLEEP', 'Sleep button', '~#javaj#~6~6   <frames> Fmain, Sleep button sample~6   <layout of Fmain>~6       EVA, 6, 6, 3, 3~6       ---,  , X~6        22, lMilliseconds, eMilli, bPressMe~6~6#data#~6~6    <eMilli> 1000~6~6#listix#~6~6    <-- bPressMe> SLEEP, @<eMilli>');INSERT INTO tLsxCmdAliases VALUES ('SLEEP', 'WAIT');INSERT INTO tlsxCmdSyntaxHead VALUES ('SLEEP', '1', '3', 'Sleep the main thread for delayMilli milliseconds');INSERT INTO tlsxCmdSyntaxParams VALUES ('SLEEP', '1', '0', 'delayMilli', '', 'Delay in milliseconds');
INSERT INTO tGastItemDoc VALUES ('STRCONV', 'listix_command', 'lang_variables', 'listix.cmds.cmdStrconvert', '3', 'String and Eva variable conversions', '~~6 A set of string, numeric, date and Eva variable conversions. Some convertions accept a~6 variable name where to place the result in and all of them can print out the result onto the~6 current Listix target (standard output, console, file or variable). Even if the conversion~6 does not have an eva variable where to put the result in, it is always possible to do that,~6 for example~6~6    <xyz>~6       SET VAR, myResult, @<upperStr>~6~6    <upperStr> STRCONV, UPPER, ~3Convert this to upper case~3~6~6 Standard conversions~6 ----------------------~6~6 The syntaxes SUBSTR, UPPER, UPPER1, LOWER, DATE-LONG, LONG-DATE, NUM-FIX, NUM-ENG are general~6 and typical data and number conversions. Note that there are also ways of using more standard~6 conversion provided by sqlite. For example to get the data a week ago~6~6    <main> //A week ago it was @<today-7>~6~6    <today-7>~6       LOOP, SQL,, //SELECT date(~4now~4, ~4-7 day~4) AS value;~6       @<value>~6~6 in this example we have used ~3LOOP, SQL~3 but note that the select use no database or table at all~6 For more sqlite functions try the links~6       http://sqlite.org/lang_corefunc.html~6       http://sqlite.org/lang_datefunc.html~6~6~6 Other conversions~6 ----------------------~6~6 The syntaxes EVA-TEXT, TEXT-EVA, GEN EVA work with Eva variables.~6 Finally STR-TEXT, STRE-TEXT and TEXT-STR are special conversions Text to string~6 and viceversa where new lines together with other few characters like ~4, ~8 are coded resulting~6 a string with no one of these special characters. These conversions are used internally in Gastona~6 native databases (sqlite) for texts and it happens transparently, nevertheless it might be~6 useful to perform them manually in some kind of applications.~6');
INSERT INTO tGastExamples VALUES ('STRCONV', 'STRCONV basicSample', '~ #javaj#~6~6    <frames> oConsole, STRCONV Basic sample~6~6 #listix#~6~6    <main0>~6       //Substring of ABCDE 3, 2 is ~3~6       STRCONV, SUBSTR, ~3ABCDEF~3, 3, 2~6       //~3~6       //~6       //Upper of AbcDef is ~3~6       STRCONV, UPPER, ~3AbcDef~3~6       //~3~6       //~6       //Upper1 of abcdef is ~3~6       STRCONV, UPPER1, ~3abcdef~3~6       //~3~6       //~6       //The text [~3Hola~3 <tio & Cia>] in html ... [~6       STRCONV, TEXT-HTMLTEXT, //~3Hola~3 <tio & Cia>~6       //]~6       //~6       SETVAR, ahora, @<:lsx date yyyy-MM-dd HH:mm:ss S>~6       //Now is @<ahora>~6       //~6       //Milliseconds since 1970 =~6       STRCONV, DATE-LONG, @<ahora>~6       //~6       //A week ago it was = (@<aweek ago>) ms since 1970 =~6       STRCONV, LONG-DATE, @<aweek ago>~6       //~6       //Normalizing a path =~6       STRCONV, PATH-SOLVE, ~4/firsr/second/./ignorethis/removeit/../.././third/./final/byedir/..~6       //~6       //Building a valid file name from a text =~6       STRCONV, TEXT-VARNAME, ~4This is~820~8NOT a valid () file name, is it?~6~6   <aweek ago>  =, along - 1000*3600*24*7~6~6   <along>  STRCONV, DATE-LONG, @<ahora>');
INSERT INTO tGastExamples VALUES ('STRCONV', 'Listix interactive sample', '~#javaj#~6~6   <frames> Fmain, Convert Text to Evas sample~6~6   <layout of Fmain>~6    EVA, 10, 10, 7, 7~6~6       , X~6       , lListix interactiv~6     X , xText~6       , bRun~6     X , oConsole~6~6    <sysDefaultFonts>~6       Consolas, 15, 0, TextArea~6~6#data#~6~6   <xText>~6      //LOOP, FOR, ii, 1, 3~6      //    ,, //Hola @<:sys user.name>, que tal andas~6~6#listix#~6~6   <-- bRun>~6      LSX, convert text into eva~6      LSX, launch new format~6~6   <convert text into eva> STRCONV, TEXT-EVA, xText, my new format~6   <launch new format>     LSX, my new format');
INSERT INTO tGastExamples VALUES ('STRCONV', 'caballoJs', '~#javaj#~6~6   <frames> main, Converting trazos to javascript canvas~6~6   <layout of main>~6    EVA, 10, 10, 7, 7~6~6       , X~6       , lTrazos~6     X , xTrazos~6       , bRun~6     X , oConsole~6~6    <sysDefaultFonts>~6       Consolas, 14, 0, TextArea~6~6#data#~6~6   <xTrazos>~6       //defstyle, piel, ~3fc:+255127039~3~6       //defstyle, pelo, ~3fc:+234234234~3~6       //~6       //z ,238, 121, ~3piel~3, //jau,84,39,109,-20,47,23,-6,54,-22,20,-35,25,-68,29,-75,1,-54,-29,-31,-81~6       //z ,196, 223, ~3piel~3, //jau,-43,-81,-10,-36,9,-19,39,8,64,37~6       //z ,155,  84, ~3piel~3, //jau,-47,7,-34,48,-16,29,20,19,36,-29,40,-15~6       //z ,468, 148, ~3piel~3, //jau,26,22,14,27,1,46,-5,49,12,56,-12,73,-7,33,-25,0,13,-32,-10,-93,-45,-57,-16,-49~6       //z ,196, 213, ~3piel~3, //jau,4,52,29,42,18,65,0,54,-12,28,10,4,18,-1,-2,-18,11,-18,-4,-85,5,-86~6       //z ,473, 152, ~3pelo~3, //jau,51,14,23,59,8,86,-10,0,-3,27,-12,-28,5,38,-11,-2,-6,-37,5,-80,-11,-45,-19,-14~6       //z ,128,  83, ~3piel~3, //jau,-22,-23,3,31~6       //z ,123,  83, ~3pelo~3, //jau,44,-3,65,19,28,27,30,25,-20,19,6,13,-15,-3,-7,-26,-13,7,-17,-24,-28,-28,-21,4,-23,-20,-31,-9~6~6#listix#~6~6   <-- bRun>~6      MSG, oConsole clear~6      STRCONV, TEXT-EVA, xTrazos, varTrazos~6      STRCONV, TRAZOS-JS, varTrazos, 0, 1000, 1000, 1~6');INSERT INTO tLsxCmdAliases VALUES ('STRCONV', 'STR');
