INSERT INTO tGastExamples VALUES ('TIMER', 'Timer print', '~#javaj#~6~6   <frames> Fmain, Timer sample~6   <layout of Fmain>~6       EVA, 6, 6, 3, 3~6       ---,  , X~6        22, lMilliseconds, eMilli, bStart, bStop~6         X, oConsola     , -, -, -~6~6#data#~6~6    <eMilli> 500~6~6#listix#~6~6    <-- bStart>~6          TIMER, START, MiTarea, @<eMilli>, 100~6~6    <-- bStop>~6          TIMER, STOP, MiTarea~6~6    <MiTarea>~6          //tick @<:lsx date2>~6          //');INSERT INTO tLsxCmdAliases VALUES ('TIMER', 'TICK');
