INSERT INTO tGastExamples VALUES ('UDP', 'udpClientServer', '~#javaj#~6~6    <frames> oConso~6~6#listix#~6~6    <main>~6       UDP, SET SERVER, cucho, 1866~6       UDP, SET CLIENT, sendo, localhost, 1866~6       UDP, SET CLIENT, vicens, localhost, 1866~6       UDP, SEND ONCE, localhost, 1866, //first datagram from anonymus!~6       UDP, AGENT SEND, sendo, //first datagram from sendo!~6       UDP, AGENT SEND, vicens, //my vicens datagram!~6~6    <-- cucho udp>~6       //Cucho: Rx ~3@<p1>~3 from @<p2>:@<p3>~6       //~6       UDP, AGENT SEND, cucho, //GOT IT port @<p3>!~6~6    <-- sendo udp>~6       //Sendo: Rx ~3@<p1>~3 from @<p2>:@<p3>~6       //~6       UDP, CLOSE, sendo~6~6    <-- vicens udp>~6       //Vicens: Rx ~3@<p1>~3 from @<p2>:@<p3>~6       //~6       UDP, CLOSE, vicens~6');
INSERT INTO tGastExamples VALUES ('UDP', 'simple Gastona UDP debug client', '~#javaj#~6~6   <frames> F, Simple gastona UDP debug client~6~6   <layout of F>~6      Eva, 10, 10, 4, 4~6~6         ,            , 120       , X       ,          , X~6         , lIP Gastona, eIPGastona, bConnect, layLevels, bClear~6         , lPort      , eUdpPort  , +       , +        , +~6       X , oCansa     , -, -, -, -~6~6   <layout of layLevels>~6      PANEL, X, Debug level~6      rgLevels~6~6#data#~6~6   <eIPGastona> 127.0.0.1~6   <eUdpPort> 11882~6~6   <rgLevels orientation> X~6   <rgLevels>~6        label, selected~6        2~6        4~6        6~6        8~6        10~6        12   , 1~6        14~6        16~6        18~6        19~6~6#listix#~6~6   <main>~6      //~6      // Very simple debug udp client for gastona applications~6      // including android gastona scripts.~6      //~6      // Your application has to open a UDP port for debug messages.~6      // This can be done by including this at the top of the script~6      //~6      //       #gastona#~6      //~6      //           <UDP_DEBUG_PORT>~6      //~6      // Then start your gastona application.~6      //~6      // Enter the right values for IP and UDP port if for example~6      // your aplication run in another computer or device or it has~6      // chosen another UDP port (gastona uses 11882 by default).~6      //~6      // Press Connect and choose the debug level.~6      //~6      // When interacting with your application you should see debug messages~6      // on the client.~6~6   <-- bConnect>~6      UDP, SET CLIENT, pincho, @<eIPGastona>, @<eUdpPort>~6      UDP, AGENT SEND, pincho, //SETLEVEL @<rgLevels selected.label>~6      SLEEP, 300~6      UDP, AGENT SEND, pincho, //ON~6~6   <-- pincho udp>~6       @<:raw p1>~6       //~6       //~6~6   <-- rgLevels>~6      UDP, AGENT SEND, pincho, //SETLEVEL @<rgLevels selected.label>~6~6   <-- bClear>~6      MSG, oCansa clear');INSERT INTO tLsxCmdAliases VALUES ('UDP', 'UDP');INSERT INTO tlsxCmdSyntaxHead VALUES ('UDP', '1', '3', 'Send a datagram to an IP Port once without expecting any response');
INSERT INTO tlsxCmdSyntaxHead VALUES ('UDP', '2', '3', 'Opens an UDP client or send-first which will establish a communication with some UDP server or listen-first');
