INSERT INTO tGastExamples VALUES ('ZIP', 'zip viewer demo', '~#javaj#~6~6   <frames> F, Zip viewer demo, 400~6~6   <layout of F>~6      EVA, 10, 10, 7, 7~6~6         ,          , X       ,~6         , lZipFile , eZipFile, bPickFile~6       X , vistas   , -       , -~6~6   <layout of vistas>~6      TABBED,~6~6      Asiste table , sAsiste~6      Tree         , aTree~6~6#data#~6~6   <lZipFile> Zip or jar file~6~6   <bPickFile DIALOG> FILE~6~6   <eZipFile> gastona.jar~6~6#listix#~6~6   <-- bPickFile>~6      -->, eZipFile data!,, @<bPickFile chosen>~6      @<get Zip Entries>~6~6   <-- eZipFile>~6      @<get Zip Entries>~6~6   <get Zip Entries>~6      ZIP, GET ENTRIES, @<eZipFile>, tabEntries~6      DATABASE,, CREATE TABLE, tabEntries~6      -->, aTree data!, sqlSelect, //SELECT * FROM tabEntries ORDER BY fileName;~6      -->, sAsiste data!, sqlSelect, //SELECT * FROM tabEntries ORDER BY fileName;~6');INSERT INTO tLsxCmdAliases VALUES ('ZIP', 'SIP');INSERT INTO tlsxCmdSyntaxHead VALUES ('ZIP', '1', '2', 'make a zip (jar) file of an entire directory');
