INSERT INTO tGastItemDoc VALUES ('z2DGraphicScenes', 'javaj_widget', 'misc', 'javaj.widgets.z2DGraphicScenes', '4', 'A canvas to draw 2D geometries with zoom and movement capabilities', '~~6 A canvas where you can place a scene composed by objects. Basically in the attribute ~3scene~3~6 you define the objects of the scene, the order will be the z-order (behind ... top). For instance~6~6    <2DsMyPaint scene>~6       background~6       stick~6       ball~6~6 then for each object an attribute ~3graphic NAME~3 will define its final aspect~6~6    <2DsMyPaint graphic background>~6       rect, ~3fill:blue~3, 0, 0, 200, 400~6~6    <2DsMyPaint graphic stick>~6       path, ~3fill:brown~3, ~3M 200 60 c 10 40 15 43 22 18 34 7 z~3~6~6    <2DsMyPaint graphic ball>~6       circle, ~3fill:green~3, 130, 100, 50~6~6 the attribute ~3graphic NAME~3 admits multiple lines and following geometries are supported~6~6    geometrie      parameters~6    -----------    ---------------------------------------------~6    path           style, path data~6    rect           style, left, top, width, height, radius x, radius y~6    polygon        style, points~6    polyline       style, points~6    circle         style, center x, center y, radius~6    ellipse        style, center x, center y, radius x, radius y~6    oval           style, left, top, width, height~6    text           style, left, right, width, ?, text~6    svgfile        name of svg file (not all that svg defines is supported but the geometries path, rect, etc)~6~6 where ~3style~3 and ~3path data~3 are very similar as defined in SVG 1.1 with slight variations~6~6~6 NOTE: This widget has been added lately and its specification, although useful, still has to improved and~6       in these changes might loose compatibility with the defined here!~6');
INSERT INTO tGastExamples VALUES ('z2DGraphicScenes', 'scene noche', '~#javaj#~6~6    <frames> f1,~6~6    <layout of f1>~6       EVA, 4, 4, 3, 3~6          , X~6        X , 2DsMyPaint~6       100, oConso~6~6#listix#~6~6    <-- graphicPress fledy>~6        //you clicked on fledy ...~6        //~6~6#data#~6~6    <2DsMyPaint scene>~6       noche~6       luna~6       fledy~6~6    <2DsMyPaint graphic noche>~6       rect, ~3fill:#440053~3, 0, 0, 600, 400~6~6    <2DsMyPaint graphic luna>~6       circle, ~3fc:#C2C2C2~3, 260, 170, 110~6~6    <2DsMyPaint graphic fledy>~6       path, ~3fc:black~3,  ~3M  190  237 j   55 -5  48 -31  61  37  -45  3 -18  27 -31 -5 -21  20 -17  23 -27 -21 -50 -4 -23 -17 -30  8  37 -48 z~3~6       path, ~3fc:black~3,  ~3M  194  240 j  -10 -18 -1 -16  11  7  23  5  26 -18  1  22 -5  15~3~6~6    <2DsMyPaint graphicPress fledy>~6       path, ~3fc:red~3,  ~3M  190  237 j   55 -5  48 -31  61  37  -45  3 -18  27 -31 -5 -21  20 -17  23 -27 -21 -50 -4 -23 -17 -30  8  37 -48 z~3~6       path, ~3fc:red~3,  ~3M  194  240 j  -10 -18 -1 -16  11  7  23  5  26 -18  1  22 -5  15~3~6       text, ~3font-family:Tahoma;fc:;fs:22~3, 180, 150, 0, 0, Cuaaaao!~6');
INSERT INTO tGastExamples VALUES ('z2DGraphicScenes', 'grid and curve', '~#javaj#~6~6   <frames> f1,~6   <layout of f1>~6      EVA, 4, 4, 3, 3~6         , X~6      X , 2DsMyPaint~6~6#listix#~6~6   <main>~6      LSX, crea grid(x0 x1 incx y0 y1 incy var), -50, 50, 10, -40, 40, 10, ~32DsMyPaint graphic grid~3~6      MSG, 2DsMyPaint data!~6~6   <crea grid(x0 x1 incx y0 y1 incy var)>~6      VAR=, @<p7>, ~3~3~6      LOOP, FOR, x, @<p1>, @<p2>, @<p3>~6         ,, VAR+, @<p7>, path, ~3~3, ~3M @<x> @<p4> L @<x> @<p5>~3~6      LOOP, FOR, y, @<p4>, @<p5>, @<p6>~6         ,, VAR+, @<p7>, path, ~3~3, ~3M @<p1> @<y> L @<p2> @<y>~3~6      VAR+, @<p7>, text, ~3font-family:Tahoma;fc:;fs:2~3, 0, 0, 0, 0, ~3~3~6      LOOP, FOR, x, @<p1>, @<p2>, @<p3>~6         ,, VAR+, @<p7>, text, ~3font-family:Tahoma;fs:2~3, @<x+.3>, 0, 0, 0, @<x>~6      LOOP, FOR, y, @<p4>, @<p5>, @<p6>~6         ,, VAR+, @<p7>, text, ~3font-family:Tahoma;fs:2~3, .3, @<y>, 0, 0, @<y>~6~6   <x+.3> =, x+.3~6~6#data#~6~6   <2DsMyPaint scene>~6      grid~6      dabajo~6~6   <2DsMyPaint graphic dabajo>~6      path, ~3fc:none;~3,  ~3M  -40 0 C -30 -40 -10 0 40 0~3~6~6');INSERT INTO tZWidgetAttributes VALUES ('z2DGraphicScenes', 'visible', 'in', '0 / 1', 'Value 0 to make the widget not visible');
