INSERT INTO tZWidgetAttributes VALUES ('z2DGraphicScenes', 'gestic', 'in', 'number', 'Determines which zoom and translations are allowed, for instance 24 allow translation of the objects and 25 zoom as well. (ZOOM_MULTITOUCH 1, ZOOM_X 2, ZOOM_Y 4, TRANS_X 8, TRANS_Y 16, MESSAGES 32)');
