INSERT INTO tZWidgetAttributes VALUES ('z2DGraphicScenes', 'scene', 'in', 'table', 'table of two columns (no header) : name of the object, movements allowed as 0/1 up,down,left,right for example 1101 means all movements except left.');
INSERT INTO tZWidgetAttributes VALUES ('z2DGraphicScenes', 'graphic NAME', 'in', 'table', 'table of geometries defining the object with name NAME, possible geometries are path, rect, circle, oval, ellipse, polygon, polyline, text and svgfile');INSERT INTO tZWidgetMessages VALUES ('z2DGraphicScenes', 'data!', 'in', 'update data');
