INSERT INTO tGastItemDoc VALUES ('zAsiste', 'javaj_widget', 'table', 'javaj.widgets.zAsiste', '8', '~A database table with search, filter and sorting capabilities (rapid prefix ~4s~4)', '~~6 An ~3Asiste Tabla~3 (assist table) is a database table or view that incorporates~6 the capabilities of searching, sorting and filtering for each column or a selection~6 of them. An edit field is placed over each column, the user can enter the filter or sort~6 criteria on it and when pressing enter a new query is performed and presented.~6~6 We show the possible values for seach, order and filter with some examples~6~6    value of~6    editfield   search result~6    ----------- ---------------------------------------------~6    casa        all records where this field contains ~3casa~3~6    casa mia    all records where this field contains ~3casa~3 and ~3mia~3 in any order~6~6    >B          order by this field and records greater or equal ~3B~3~6    <Z          reverse order by this field and records less or equal ~3Z~3~6    ]B          order by this field and records greater or equal ~3B~3 and less than ~3C~3~6    [B          reverse order by this field and records less or equal ~3B~3 and greater than ~3A~3~6~6    )44         order by this field numerically and records biger or equal 44~6    (44         reverse order by this field numerically and records lower or equal 44~6~6    ~4>          records containing ~3>~3 (e.g. searching <>[]()~4 characters at begining of text)~6~6 The combination of more criteria is solved with AND operation, that means all records has to~6 comply all criterias.~6~6 Widget characteristics: ~3Common~3, ~3Table select~3 and ~3Table from database~3.~6');
INSERT INTO tGastExamples VALUES ('zAsiste', 'assist basic example', '~#javaj#~6~6    <frames> F, Hello zAsisteTabla~6~6    <layout of F>~6          PANEL, Y~6          sAsiste, lSelectInfo~6~6#data#~6~6    <sAsiste sqlSelect> //SELECT * FROM someData~6~6#listix#~6~6    <main0>~6       DATABASE,,CREATETABLE, someData~6~6    <someData>~6          id , name,     city           , age~6          718, Ricardo,  San Francisco  , 12~6          321, Abelardo, Betanzos       , 45~6          112, Joseba  , Bilbo          , 21~6~6    <-- sAsiste>~6       -->, lSelectInfo data!,, //Last selected @<sAsiste selected.name> from @<sAsiste selected.city> is @<sAsiste selected.age> years old~6~6');
INSERT INTO tGastExamples VALUES ('zAsiste', 'assist sample gastonaDoc', '~#javaj#~6~6   <frames> sAsiste, zAsiste example, 500, 300~6~6#listix#~6~6   <main>~6      DB,, EXECUTE, @<:infile META-GASTONA/WelcomeGastona/gastonaDocScript.sql>~6      -->, sAsiste data!, sqlSelect, //SELECT name, desc FROM tGastItemDoc');
INSERT INTO tGastExamples VALUES ('zAsiste', 'assist sample gastonaDoc plus export', '~#gastona#~6~6   <fusion>~6       META-GASTONA/utilApp/std/ExportBasicDialog.gasti~6~6#javaj#~6~6   <frames> fMain, zAsiste example, 500, 300~6~6   <layout of fMain>~6       EVA, 10, 10, 10, 10~6~6          ,  X~6         X, sAsiste~6          , layExportBasic~6~6#listix#~6~6   <main>~6      DB,, EXECUTE, @<:infile META-GASTONA/WelcomeGastona/gastonaDocScript.sql>~6      -->, sAsiste data!, sqlSelect, //SELECT name, desc FROM tGastItemDoc~6      LISTIX, ExportBasic set (gridName dbName), sAsiste, ~3~3~6');INSERT INTO tZWidgetAttributes VALUES ('zAsiste', 'visible', 'in', '0 / 1', 'Value 0 to make the widget not visible');
