INSERT INTO tZWidgetAttributes VALUES ('zAsiste', 'dbName', 'in', '(file name)', 'If specified then it is the database name from which the data will be retrieved (default is the global temporary database)');
INSERT INTO tZWidgetAttributes VALUES ('zAsiste', 'sqlSelect', 'in', '(sqlite select or pragma)', 'If specified SQL query where the data will be retrieved (see dbName)');
