INSERT INTO tGastItemDoc VALUES ('zButton', 'javaj_widget', 'button', 'javaj.widgets.zButton', '10', '~A typical button, also useful as file chooser dialog facility (rapid prefix ~4b~4)', '~~6 Typical button whose data is the label, an image (png, gif or jpeg) can be associated as well.~6 A special characteristic of this widget is that can launch dialogs for file(s) and directory(ies)~6 chooser. The attributes associated with this feature are ~4DIALOG~4 and ~4chosen~4, if ~4DIALOG~4~6 is given then the button acts as dialog and in the attribute ~4chosen~4 will return the chosen~6 item(s).~6~6 Note : If not given in #data# the button takes its label from its own name. This is only a~6        facility for the development and serves only as default label.Do not confuse button~4s~6        name with its label, among other things widget names cannot be changed.~6~6 Widget characteristics: ~3Common~3 and ~3Drop files and directories~3.~6');
INSERT INTO tGastExamples VALUES ('zButton', 'hello zButton', '~#javaj#~6~6    <frames> bMyButton, Hello zButton~6~6~6#data#~6~6    <bMyButton>       ~3Press the button~3~6    <bMyButton image> javaj/img/ok.png~6~6#listix#~6~6    <-- bMyButton>~6       SET VAR, bMyButton, ~3Ok, you did it!~3~6       MSG, bMyButton data!~6');
INSERT INTO tGastExamples VALUES ('zButton', 'single file chooser', '~#javaj#~6~6   <frames> F, Example Buttons~6~6   <layout of F>~6         EVA, 10, 10, 4, 4~6~6      ---, A              , 300     , X  , A~6       A , lChosen file   , eField  , -  , bFChooser~6         , lManual setting, eManual , -~6~6#data#~6~6   <bFChooser DIALOG> FILE~6   <bFChooser widget> eField~6   <bFChooser image> javaj\img\leaf.png~6~6#listix#~6~6   <-- bFChooser>~6      -->, eManual data!,, @<bFChooser chosen>~6');
