INSERT INTO tZWidgetAttributes VALUES ('zButton', 'visible', 'in', '0 / 1', 'Value 0 to make the widget not visible');
INSERT INTO tZWidgetAttributes VALUES ('zButton', 'enabled', 'in', '0 / 1', 'Value 0 to disable the widget');
INSERT INTO tZWidgetAttributes VALUES ('zButton', 'image', 'in', 'fileName', 'File name of the image (png, gif or jpg)');
INSERT INTO tZWidgetAttributes VALUES ('zButton', 'graffiti', 'in', 'EvaName', 'Eva variable containing the painting (set of svgfile, path, polygon, polyline, rect,circle, oval, ellipse, text). The resulting painting will be resized to fit on the value of the property gastona.defaultButtonPaintingSize (16)');
INSERT INTO tZWidgetAttributes VALUES ('zButton', 'mnemonic', 'in', 'char', 'Mnemonic for the button (see java setMnemonic)');
INSERT INTO tZWidgetAttributes VALUES ('zButton', 'DIALOG', 'in', '~FILE ~2 FILES ~2 DIR ~2 DIRS', 'The button launches a file chooser dialog of one file or multiple files or one directory or multiple directories');
INSERT INTO tZWidgetAttributes VALUES ('zButton', 'DIALOG FILTER', 'in', 'extension', '@todo documentar esto');
INSERT INTO tZWidgetAttributes VALUES ('zButton', 'widget', 'in', 'zWidget name', 'zWidget name (usually edit text) associated with the FILE or DIR dialog (only single selection). If set the value of the selection is set automatically into the widget');
INSERT INTO tZWidgetAttributes VALUES ('zButton', 'chosen', 'out', 'string or list', '~If attribute ~4DIALOG~4 is given, this attribute holds the chosen file(s) or directory(ies). In case of multiselection the attribute is a Eva table (fullPath).');
INSERT INTO tZWidgetAttributes VALUES ('zButton', 'droppedFiles', 'out', '(Eva table)', 'Eva table (pathFile,fileName,extension,fullPath,date,size) containing all dropped files. Note that this attribute has to exist in order to enable drag & dropping files into this Component.');
INSERT INTO tZWidgetAttributes VALUES ('zButton', 'droppedDirs', 'out', '(Eva table)', 'Eva table (pathFile,fileName,extension,fullPath,date,size) containing all dropped directories. Note that this attribute has to exist in order to enable drag & dropping files into this Component.');INSERT INTO tZWidgetMessages VALUES ('zButton', 'data!', 'in', 'update data');
INSERT INTO tZWidgetMessages VALUES ('zButton', 'control!', 'in', 'update control');
INSERT INTO tZWidgetMessages VALUES ('zButton', '', 'out', 'button has been pressed');
INSERT INTO tZWidgetMessages VALUES ('zButton', 'droppedFiles', 'out', '~If files drag & drop enabled this message indicates that the user has dropped files (see attribute ~4droppedFiles~4)');
INSERT INTO tZWidgetMessages VALUES ('zButton', 'droppedDirs', 'out', '~If directories drag & drop is enabled this message indicates that the user has dropped directories (see attribute ~4droppedDirs~4)');
INSERT INTO tGastItemDoc VALUES ('zCheckBox', 'javaj_widget', 'button', 'javaj.widgets.zCheckBox', '8', 'A typical check box (rapid prefix k)', '~~6 Typical checkbox. The attribute ~4selected~4 indicates the state of the checkbox.~6~6 Note : If not given in #data# the widgets takes its label from its own name. This is only a~6        facility for the development and serves only as default label. Do not confuse widget~4s~6        name with its contents, among other things widget names cannot be changed.~6~6 Widget characteristics: ~3Common~3.~6');
INSERT INTO tGastExamples VALUES ('zCheckBox', 'hello zCheckBox', '~#javaj#~6~6    <frames> F, Hello zCheckBox~6~6    <layout of F>~6          EVA, 30, 30, 10, 10~6          , A~6          , lState~6          , kCheck~6~6#data#~6~6    <kCheck> ~3Check or uncheck~3~6~6#listix#~6~6    <-- kCheck>~6       IN CASE,  @<kCheck selected>~6              ,   1, SET VAR, lState, ~3Checked!~3~6              ,   0, SET VAR, lState, ~3NOT checked!~3~6       MSG, lState data!~6');INSERT INTO tZWidgetAttributes VALUES ('zCheckBox', '', 'in', '(String)', 'Caption of the checkbox');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBox', 'visible', 'in', '0 / 1', 'Value 0 to make the widget not visible');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBox', 'enabled', 'in', '0 / 1', 'Value 0 to disable the widget');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBox', 'selected', 'inout', '~0 ~2 1', 'Value 0 not checked, 1 checked');INSERT INTO tZWidgetMessages VALUES ('zCheckBox', '', 'out', 'check box has been pressed');
INSERT INTO tZWidgetMessages VALUES ('zCheckBox', 'data!', 'in', 'update data');
INSERT INTO tZWidgetMessages VALUES ('zCheckBox', 'control!', 'in', 'update control');
INSERT INTO tGastItemDoc VALUES ('zCheckBoxTable', 'javaj_widget', 'button', 'javaj.widgets.zCheckBoxTable', '7', '~Group of several check boxes group in one widget (rapid prefix ~4kg~4)', '~~6 This widget manages a complete check box group and it is a good alternative to use zCheckBox~6 and the layout RADIO. The labels as well as any other convenient data associated with the radio~6 buttons of the group are given in a table.~6~6 Widget characteristics: ~3Common~3.~6');
INSERT INTO tGastExamples VALUES ('zCheckBoxTable', 'checkBoxTable sample', '~#javaj#~6~6    <frames> F, Hello zCheckBoxTable~6~6    <layout of F>~6       PANEL, Y~6       kgChecks, oConsole~6~6#data#~6~6    <kgChecks orientation> X~6    <kgChecks>~6        label , value1 , value0  , selected, desc1      , desc0~6        Opt1  , 161    ,   -11   , 1       , is on!     , is off!~6        Opt2  , 102    ,   -22   , 0       , encendida  , apagada~6        Opt3  , 113    ,   -33   , 1       , an         , aus~6        Opt4  , 144    ,   -44   , 0       , set la vie , je ne sais pas~6~6#listix#~6~6    <main> LSX, show~6~6    <-- kgChecks> LSX, show~6~6    <show>~6       MSG, oConsole clear~6       LOOP, VAR, kgChecks~6           ,, //item @<label> is @<selected>, now value @<valueOnOff>, desc @<descOnOff>~6       //~6       //current data~6       DUMP, data,, kgChecks~6~6    <show selected>~6       CHECK, VAR, kgChecks subTableSelection~6       LOOP, VAR, kgChecks subTableSelection~6           ,, //item @<label> is @<selected>, now value @<value1> (@<value0>), desc @<desc1> (@<desc0>)~6       //~6~6    <valueOnOff>   VALUE OF, value@<selected>~6    <descOnOff>    VALUE OF, desc@<selected>');INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', '', 'in', 'text', 'Caption text of the radio button');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'var', 'in', 'Eva name', '~Reference to variable containing the data for this widget. If this atribute is given it will mask the data contained in the attribute ~3~3 (usually the data)');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'visible', 'in', '0 / 1', 'Value 0 to make the widget not visible');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'enabled', 'in', '0 / 1', 'Value 0 to disable the widget');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'orientation', 'in', 'X / Y', 'Horizontal or vertical (default) orientation');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'titledBorder', 'in', 'text', 'If given a titled border will be placed');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'dbName', 'in', 'file name', 'If specified then it is the database name from which the data will be retrieved (default is the global temporary database)');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'sqlSelect', 'in', 'sqlite select or pragma', 'If specified then the data will be retrieved from a database (see dbName)');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'sqlExtraFilter', 'in', 'part of sqlite select', 'Filter that is added to the query');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'subTableSelection', 'out', 'Eva table', 'a table formed with the selected rows');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'LABELVALUE.selected', 'out', 'String', 'Returns 0 or 1 depending on if the check box identified by the label LABELVALUE is selected or not');
INSERT INTO tZWidgetAttributes VALUES ('zCheckBoxTable', 'LABELVALUE.COLUMNONOFF', 'out', 'String', 'Returns the value of the column named COLUMNONOFOn or COLUMNONOFOff depending on if the check box identified by the label LABELVALUE is selected or not');INSERT INTO tZWidgetMessages VALUES ('zCheckBoxTable', 'data!', 'in', 'update data');
INSERT INTO tZWidgetMessages VALUES ('zCheckBoxTable', 'control!', 'in', 'update control');
INSERT INTO tZWidgetMessages VALUES ('zCheckBoxTable', 'select!', 'in', '~select records. Example ~3MSG, widget select!, #, 2, 4~3 select the records 2 and 4 (0 based) and ~3MSG, widget select!, name, Barcelona~3 selects the first record which name is ~3Barcelona~3');
INSERT INTO tZWidgetMessages VALUES ('zCheckBoxTable', '', 'out', 'a check box in the group has been changed');
INSERT INTO tGastItemDoc VALUES ('zComboBox', 'javaj_widget', 'table', 'javaj.widgets.zComboBox', '7', '~A combo box list with database capabilities (rapid prefix ~4c~4)', '~~6 Combo box has, like list and tables, the Table data model. This is a convenient way of~6 representing an item since we can represent more ~3dimensions~3 of each selection facilitating~6 the implementation of the logic. For example we might present to the user the options ~3Less~3,~6 ~3More~3 and ~3No limit~3 having these values the logic meaning for us of 10, 50 and 9000.~6 Then we simply build the data as the table~6~6       <cMyCombox>~6          text        , limit~6          ~3Less~3      , 10~6          ~3More~3      , 50~6          ~3No limit~3  , 9000~6~6 and when the user chooses the item ~3More~3 we have the limit onto the variable~6 @<cMyCombox selected.limit> (attribute selected.COLUMN).~6~6 And talking about limitations, note that although the data can be retrieved from a database,~6 in this widget big selections are limited to about 1000 elements for some technical reasons.~6 In this widget ~3automatic database windowing~3 cannot be used as it is in zTable or zAsiste~6 widgets. So please use zTable or better zAsiste for big table results. Furthermore for a~6 combo box a huge list would not make much sense, specially for the user!~6~6 Widget characteristics: ~3Common~3, ~3Table select~3 and ~3Table from database~3.~6');
INSERT INTO tZWidgetAttributes VALUES ('zComboBox', '', 'in', '(Eva Table)', 'Contents of items');
INSERT INTO tZWidgetAttributes VALUES ('zComboBox', 'visible', 'in', '0 / 1', 'Value 0 to make the widget not visible');
INSERT INTO tZWidgetAttributes VALUES ('zComboBox', 'enabled', 'in', '0 / 1', 'Value 0 to disable the widget');
INSERT INTO tZWidgetAttributes VALUES ('zComboBox', 'var', 'in', 'Eva name', '~Reference to variable containing the data for this widget. If this atribute is given it will mask the data contained in the attribute ~3~3 (usually the data)');
INSERT INTO tZWidgetAttributes VALUES ('zComboBox', 'visibleColumns', 'in', '(Eva list)', 'Comma separated list of column names');
INSERT INTO tZWidgetAttributes VALUES ('zComboBox', 'subTableSelection', 'out', 'Eva table', 'a table formed with the selected rows');
INSERT INTO tZWidgetAttributes VALUES ('zComboBox', 'selected.COLNAME', 'out', 'String', 'Contents of the column with name COLNAME of first selected row of the table. There is one variable select.COLNAME for each column in the table (inclusive the not visible columns)');
INSERT INTO tZWidgetAttributes VALUES ('zComboBox', 'dbName', 'in', 'file name', 'If specified then it is the database name from which the data will be retrieved (default is the global temporary database)');
INSERT INTO tZWidgetAttributes VALUES ('zComboBox', 'sqlSelect', 'in', 'sqlite select or pragma', 'If specified then the data will be retrieved from a database (see dbName)');
INSERT INTO tZWidgetAttributes VALUES ('zComboBox', 'sqlExtraFilter', 'in', 'part of sqlite select', 'Filter that is added to the query');INSERT INTO tZWidgetMessages VALUES ('zComboBox', '', 'out', 'an Item has been selected');
INSERT INTO tZWidgetMessages VALUES ('zComboBox', 'data!', 'in', 'update data');
INSERT INTO tZWidgetMessages VALUES ('zComboBox', 'control!', 'in', 'update control');
INSERT INTO tGastItemDoc VALUES ('zConsole', 'javaj_widget', 'text', 'javaj.widgets.zConsole', '6', '~An output console for standard output and standard error streams (rapid prefix ~4o~4)', '~~6 Output console for standard output and/or standard error streams. These streams are used by~6 a lot of programs specially command line programs. In Gastona, listix use the standard output~6 stream as default file for its generation (see listix command GENERATE). Also intern debug~6 and error messages of Gastona use these streams, therefore it is a good idea to include~6 somewhere a console in your gastona application during the development to track possible~6 errors.~6');
INSERT INTO tGastExamples VALUES ('zConsole', 'hello zConsole', '~#javaj#~6~6    <frames> oConsola, Hello oConsola~6~6#listix#~6~6    <main>~6      //This is ~3hello zConsole~3 gastona script!~6      //~6');
INSERT INTO tGastExamples VALUES ('zConsole', 'all consoles', '~#javaj#~6~6    <frames> F, Hello oConsola~6~6    <layout of F>~6          EVA, 10, 10, 5, 5~6          --,    A   , X~6            , lConsole for standard output (o1Consola)~6          X , o1Consola, -~6            , lConsole for standard errors (o2Consola)~6          X , o2Consola, -~6            , lConsole for both (oConsola)~6          X , oConsola, -~6          X ,   +~6            , mandos  , -~6~6   <layout of mandos>~6      EVA, 5, 5, 7, 7~6~6         ,                ,                , X,~6         , kstdOut plugged, kstdErr plugged,  , bPrint Hello, bProduce an Error, bCall Dir, bClear Consoles~6~6#data#~6~6    <kstdOut plugged selected> 1~6    <kstdErr plugged selected> 1~6~6#listix#~6~6    <-- bPrint Hello>~6       //HELLO CONSOLE!~6       //~6~6    <-- bCall Dir>~6       CHECK, LINUX, CALL, //CMD /C dir~6       CALL, ls -la~6~6    <-- bProduce an Error>~6       PLEASE WORK,,~6~6    <-- bClear Consoles>~6       MSG, o1Consola clear~6       MSG, o2Consola clear~6       MSG, oConsola clear~6~6    <-- kstdOut plugged>~6       IN CASE, @<kstdOut plugged selected>~6              , 1, MSG, oConsola plugStd~6              , 0, MSG, oConsola unplugStd~6~6    <-- kstdErr plugged>~6      IN CASE, @<kstdErr plugged selected>~6             , 1, MSG, oConsola plugErr~6             , 0, MSG, oConsola unplugErr');INSERT INTO tZWidgetAttributes VALUES ('zConsole', 'visible', 'in', '0 / 1', 'Value 0 to make the widget not visible');
INSERT INTO tZWidgetAttributes VALUES ('zConsole', 'enabled', 'in', '0 / 1', 'Value 0 to disable the widget');
INSERT INTO tZWidgetAttributes VALUES ('zConsole', 'fileName', 'in', 'file name', '~File to be used in message ~3save~3');INSERT INTO tZWidgetMessages VALUES ('zConsole', 'save', 'in', '~saves the contents to the file given either as parameter or given in the attribute ~3fileName~3');
INSERT INTO tZWidgetMessages VALUES ('zConsole', 'clear', 'in', 'clears the contents');
INSERT INTO tZWidgetMessages VALUES ('zConsole', 'autoScroll', 'in', 'restore autoScroll mode. Autoscroll is automatically disabled when the user click on the console in any line but the last one.');
INSERT INTO tZWidgetMessages VALUES ('zConsole', 'plugStd', 'in', 'plug the standard output (System.out)');
INSERT INTO tZWidgetMessages VALUES ('zConsole', 'plugErr', 'in', 'plug the standard error  (System.err)');
INSERT INTO tZWidgetMessages VALUES ('zConsole', 'unplugStd', 'in', 'unplug the standard output (System.out)');
