INSERT INTO tGastItemDoc VALUES ('zEditField', 'javaj_widget', 'text', 'javaj.widgets.zEditField', '10', '~An edit field (rapid prefix ~4e~4)', '~~6 A typical edit field. The only envent triggered in this widget is the empty message~6 which is sent when pressing enter on the widget.~6~6 Widget characteristics: ~3Common~3~6');
