INSERT INTO tGastItemDoc VALUES ('zLeds', 'javaj_widget', 'misc', 'javaj.widgets.zLeds', '3', '~A set of up to three lights (rapid prefix ~4g~4)', '~~6 A set of up to three lights or leds of rectangular form that can be used as indicators of~6 any activity. Each led can be set to 4 possible colors throug messages.~6~6 At present the number of leds per default is three and it can only be changed using the~6 <javaClass of ...> instanciation mechanism. For example:~6~6    <layout of myLedsSet>  javaj.widgets.zLeds, 2~6~6    where ~4myLedsSet~4 is supossed to be the widget name~6~6 Signaling Gastona intern processing~6 -----------------------------------~6~6 This widget might easily reflect some processing states. For example there are some time~6 consuming processes like sqlite queries, scaning files or parsing text that are signalized~6 using Mensaka messages. We can catch these messages and convert them to our widget messages~6 using the javaj variable <MessageToMessage>. For example~6~6   <MessageToMessage>~6~6      _lib db.sqlite.callStart  , gLus L12~6      _lib db.sqlite.callEnd    , gLus L11~6      _lib db.sqlite.callError  , gLus L13~6~6      ledMsg scanFiles_start    , gLus L22~6      ledMsg scanFiles_end      , gLus L21~6~6      ledMsg parsing_start      , gLus L32~6      ledMsg parsing_end        , gLus L31~6');
INSERT INTO tGastExamples VALUES ('zLeds', 'hello zLeds', '~   #listix#~6       <-- iAll>~6          MSK, gLights @<iAll selected.msg>~6~6   #data#~6       <iAll>~6          msg~6          L10~6          L11~6          L12~6          L13~6          L20~6          L21~6          L22~6          L23~6          L30~6          L31~6          L32~6          L33~6~6   #javaj#~6~6       <frames> F, Hello zLeds, 200, 200~6~6       <layout of F>~6             PANEL, X~6             iAll, gLights');INSERT INTO tZWidgetAttributes VALUES ('zLeds', 'visible', 'in', '0 / 1', 'Value 0 to make the widget not visible');
