INSERT INTO tGastItemDoc VALUES ('zList', 'javaj_widget', 'table', 'javaj.widgets.zList', '9', '~A list that supports memory tables or db queries (rapid prefix ~4i~4)', '~~6 List which data can be given as a Table in memory (Eva table) or can be retrieved from a~6 database using a sql Select query (see zComboBox and ~3Table select~3 and ~3Table from database~3~6 widget characteristics).~6~6 Widget characteristics: ~3Common~3, ~3Table select~3, ~3Table from database~3 and ~3Files droppable~3.~6');
