INSERT INTO tGastItemDoc VALUES ('zMiniEditor', 'javaj_widget', 'text', 'javaj.widgets.zMiniEditor', '9', '~A text area strictly associated with a file (rapid prefix ~4f~4)', '~~6 Text area widget like zTextArea (prefix ~4x~4) with the limitation that the text content is not~6 hold in any data variable. Text content is loaded directly from a file, the user might change~6 it and it can be saved into a file. This is more efficient as zTextArea specially for big~6 files.~6~6 Widget characteristics: ~3Common~3 and ~3Text data~3~6');
INSERT INTO tGastExamples VALUES ('zMiniEditor', 'little editor', '~#javaj#~6~6    <frames> F, little Editor~6~6    <layout of F>~6          EVA, 6, 6, 3, 3~6~6          ---,          , X~6             , lFile    , eFile, bSearch, bReload, bSave~6           X , fMiniEdit, -, -, -, -~6~6    <sysDefaultFonts>~6       Consolas, 15, 0, TextField~6~6#data#~6~6    <bSearch DIALOG> FILE~6~6    <! DISABLED FOR SAFE TESTING~6    <bSave enabled> 0~6~6    <bSearch image> javaj/img/linterna.png~6    <bReload image> javaj/img/Fichero_2_2.png~6    <bSave image>   javaj/img/floppy.png~6~6#listix#~6~6    <-- bSearch>~6       -->, eFile data!,, @<bSearch chosen>~6       @<load file>~6~6    <-- bReload>~6       @<load file>~6~6    <load file>~6       CHECK, VAR  , eFile   , @<error NoFileName>~6       CHECK, RFILE, @<eFile>, @<error FileName does not exist>~6       SETVAR, fMiniEdit fileName, @<eFile>~6       MSG, fMiniEdit load~6~6    <-- bSave>~6       SETVAR, fMiniEdit fileName, @<eFile>~6       MSG, fMiniEdit save~6~6    <error NoFileName>~6       //Enter a file name, then press Load~6       //~6~6    <error FileName does not exist>~6       //File name ~3@<eFile>~3 not found!~6       //~6');INSERT INTO tZWidgetAttributes VALUES ('zMiniEditor', '', 'in', '(Eva Table)', 'Contents of items');
