INSERT INTO tGastItemDoc VALUES ('zRadioButton', 'javaj_widget', 'button', 'javaj.widgets.zRadioButton', '2', '~A typical radio button (rapid prefix ~4r~4)', '~~6 NOTE: the new widget zRadioButtonTable is a better approach to handle radio buttons~6~6 This widget represents a single radio button, note though that an isolated radio button~6 make no sense, it has to be grouped with at least another radio button. This is done through~6 the special javaj layout RADIO. Radio buttons listen and send its own messages, the RADIO~6 layout send also a message for the group.~6~6 Note : If not given in #data# the widgets takes its contents from its own name. This is only a~6        facility for the development and serves only as default label. Do not confuse widget~4s~6        name with its contents, among other things widget names cannot be changed.~6~6 Widget characteristics: ~3Common~3.~6');
INSERT INTO tGastExamples VALUES ('zRadioButton', 'hello zRadioButton', '~#javaj#~6~6    <frames> F, Hello zRadioButton~6~6    <layout of F>~6       PANEL, X~6       Fradio, oConsole~6~6    <layout of Fradio>~6          RADIO, Y~6          rOpt1, rOpt2, rOpt3, rOpt4~6~6#listix#~6~6    <-- Fradio>~6       //selected radiobutton @<Fradio selected> (option 1 state @<rOpt1 selected>)~6       //~6~6    <-- rOpt4>~6       //Option 4 has been pressed!~6       //');INSERT INTO tZWidgetAttributes VALUES ('zRadioButton', '', 'in', 'text', 'Caption text of the radio button');
