INSERT INTO tGastItemDoc VALUES ('zRadioButtonTable', 'javaj_widget', 'button', 'javaj.widgets.zRadioButtonTable', '7', '~Radio group all in one widget (rapid prefix ~4rg~4)', '~~6 This widget manages a complete radio button group and it is a good alternative to use zRadioButton~6 and the layout RADIO. The labels as well as any other convenient data associated with the radio~6 buttons of the group are given in a table.~6~6 Widget characteristics: ~3Common~3.~6');
