INSERT INTO tGastItemDoc VALUES ('zTable', 'javaj_widget', 'table', 'javaj.widgets.zTable', '7', '~A table from memory (Eva) or database (rapid prefix ~4t~4)', '~~6 Table which data can be given as a Table in memory (Eva table) or can be retrieved from a~6 database using a sql Select query (see ~3Table select~3 and ~3Table from database~3 widget~6 characteristics).~6~6 In this widget the select query might be given without limits since an automatic mechanism~6 called ~3windowing~3 takes care of retrieving the data in small blocks. For example you can~6 query a database table containing millions of registers just with ~3SELECT * FROM theTable;~3.~6~6 For facilitating the data exploration to the user it is recommended to use zAsiste instead~6 of zTable.~6~6 Widget characteristics: ~3Common~3, ~3Table select~3 and ~3Table from database~3.~6');
INSERT INTO tGastExamples VALUES ('zTable', 'hello zTable', '~#javaj#~6~6    <frames> tTabla, Hello zTable~6~6#data#~6~6    <tTabla>~6       id, name, telephone~6~6       12, Michael,  5563~6       56, Evariste, 1811~6      231, Alea,     2112~6      108, Carl,     3222~6');
INSERT INTO tGastExamples VALUES ('zTable', 'hello zTable select', '~#javaj#~6~6    <frames> tTabla, Hello zTable select~6~6#data#~6~6    <tTabla>~6       id, name, telephone~6~6       12, Michael,  5563~6       56, Evariste, 1811~6      231, Alea,     2112~6      108, Carl,     3222~6~6#listix#~6~6    <-- tTabla>~6       //~6       //~6       //First selected ~3@<tTabla selected.name>~3~6       //All selected row(s) :~6       //~6       LOOP, EVA, tTabla subTableSelection~6          ,, //    @<name> (id = @<id>)');
INSERT INTO tGastExamples VALUES ('zTable', 'hello zTable select2', '~#javaj#~6~6    <frames> F, Hello zTable select 2  , 400~6~6    <layout of F>~6          EVA, 5, 5, 3, 3~6~6             , X   , X~6             , lMain table, -~6           X , tTabla, -~6             , lSelected elements table, -~6           X , tTablaSel, -~6             , bPre sel1, bPre sel2~6~6#data#~6~6    <tTabla>~6       id, name, telephone~6~6       12, Michael,  5563~6       56, Evariste, 1811~6      231, Alea,     2112~6      108, Carl,     3222~6~6    <tTablaSel var> tTabla subTableSelection~6~6#listix#~6~6    <-- tTabla>~6       @<update table selection>~6~6    <-- bPre sel1>~6       MSG, tTabla select!, #1, 1, 3~6       @<update table selection>~6~6    <-- bPre sel2>~6       MSG, tTabla select!, ~3name~3, Evariste, Alea, Carl~6       @<update table selection>~6~6    <update table selection>~6       MSG, tTablaSel data!');
INSERT INTO tGastExamples VALUES ('zTable', 'zTable database', '~#javaj#~6~6    <frames> F, zTable database demo~6~6    <layout of F>~6          EVA, 10, 10, 4, 4~6          - , X~6          A , lSelected~6          X , tTabla~6~6#data#~6~6    <tTabla sqlSelect> //SELECT * FROM someData;~6~6    <someData>~6          id , name,     city           , age~6          718, Ricardo,  San Francisco  , 12~6          321, Abelardo, Betanzos       , 45~6          112, Joseba  , Bilbo          , 21~6~6#listix#~6~6    <main0>~6       DATABASE,,CREATETABLE, someData~6~6    <-- tTabla>~6       -->, lSelected data!,, @<tTabla selected.name> lives in @<tTabla selected.city>~6');
INSERT INTO tGastExamples VALUES ('zTable', 'tabla de multiplicar', '~#javaj#~6~6   <frames>~6      F, Tablas de multiplicar~6~6   <layout of F>~6      EVA, 10, 10, 4, 4~6~6      ---,   A~6         , digits~6        X, tMultTable~6~6   <layout of digits>~6      EVA, 4, 4, 2, 2~6~6      ---,~6         , b1, b2, b3,~6         , b4, b5, b6,~6         , b7, b8, b9~6~6#listix#~6~6   <main0>  listix, calculaTabla, 1~6~6   <-- b1>  listix, calculaTabla, 1~6   <-- b2>  listix, calculaTabla, 2~6   <-- b3>  listix, calculaTabla, 3~6   <-- b4>  listix, calculaTabla, 4~6   <-- b5>  listix, calculaTabla, 5~6   <-- b6>  listix, calculaTabla, 6~6   <-- b7>  listix, calculaTabla, 7~6   <-- b8>  listix, calculaTabla, 8~6   <-- b9>  listix, calculaTabla, 9~6~6   <calculaTabla>~6~6      SET VAR, tMultTable, @<p1> x ..., ~3~3, ~3 ~3~6      LOOP, FOR, ii, 1, 9~6          ,, @<setvalue>~6      MSG, tMultTable data!~6~6   <setvalue>    ADD TO VAR, tMultTable, ~3@<p1> x @<ii>~3, ~3=~3, @<calculaVal>~6   <calculaVal>  =, p1 * ii~6');INSERT INTO tZWidgetAttributes VALUES ('zTable', '', 'in', 'Eva table', 'Contents of the table or cache in case of db table');
