INSERT INTO tGastExamples VALUES ('PERL', 'calling perl2', '~#javaj#~6~6   <frames> main, Perl~6~6   <layout of main>~6      EVA, 10, 10, 4, 4~6~6         , X~6       X , xPerla~6         , bRun~6       X , oConso~6~6   <sysDefaultFonts>~6      Consolas, 13, 0, TextArea~6~6#data#~6~6   <xPerla>~6     //#!/usr/bin/perl~6     //~6     //print ~3gastona guys:\n~3;~6     //~6     //@guys = (~3javaj~3);~6     //~6     //print ~3@guys\n~3;~6     //~6     //push (@guys, ~3lsx+sqlite~3);~6     //~6     //print ~3@guys\n~3;~6     //~6     //unshift(@guys, ~3gastona~3);~6     //pop (@guys);~6     //push (@guys, ~3listix~3);~6     //push (@guys, ~3sqlite~3);~6     //~6     //print ~3@guys\n~3;~6     //~6     //push (@guys, ~3ruby~3);~6     //print ~3@guys\n~3;~6     //~6     //push (@guys, ~3lua~3);~6     //print ~3@guys\n~3;~6     //~6     //push (@guys, ~3perl~3);~6     //print ~3@guys\n~3;~6     //~6~6#listix#~6~6   <-- bRun>~6      MSG, oConso clear~6      PERL, @<xPerla>');INSERT INTO tLsxCmdAliases VALUES ('PERL', 'PERL');
