INSERT INTO tGastExamples VALUES ('PYTHON', 'calling python2', '~#javaj#~6~6   <frames> main, Monty~6~6   <layout of main>~6      EVA, 10, 10, 4, 4~6~6         , X~6       X , xMonty~6         , bRun~6       X , oConso~6~6   <sysDefaultFonts>~6      Consolas, 13, 0, TextArea~6~6#data#~6~6   <xMonty>~6      //def quickSort(arr):~6      //     less = []~6      //     pivotList = []~6      //     more = []~6      //     if len(arr) <= 1:~6      //         return arr~6      //     else:~6      //         pivot = arr[0]~6      //         for i in arr:~6      //             if i < pivot:~6      //                 less.append(i)~6      //             elif i > pivot:~6      //                 more.append(i)~6      //             else:~6      //                 pivotList.append(i)~6      //         less = quickSort(less)~6      //         more = quickSort(more)~6      //         return less + pivotList + more~6      //~6      //a = [4, 65, 2, -31, 0, 99, 83, 782, 1]~6      //a = quickSort(a)~6      //~6      //print (a)~6      //~6~6#listix#~6~6   <-- bRun>~6      MSG, oConso clear~6      PYTHON, @<xMonty>');INSERT INTO tLsxCmdAliases VALUES ('PYTHON', 'MONTY');INSERT INTO tlsxCmdSyntaxHead VALUES ('PYTHON', '1', '2', 'Makes a call to python binary with the given script, the output will be written in the current listix target');INSERT INTO tlsxCmdSyntaxParams VALUES ('PYTHON', '1', '0', '[python expresion]', '', 'Python expresion');INSERT INTO tlsxCmdSyntaxOpts VALUES ('PYTHON', '1', 'BODY', 'pythonCode', '', 'Python code');
