INSERT INTO tGastExamples VALUES ('RUBY', 'calling ruby2', '~#javaj#~6~6   <frames> main, Rubio~6~6   <layout of main>~6      EVA, 10, 10, 4, 4~6~6         , X~6       X , xRubio~6         , bRun~6       X , oConso~6~6   <sysDefaultFonts>~6      Consolas, 13, 0, TextArea~6~6#data#~6~6   <xRubio>~6      // class Jello~6      //   def initialize(name = ~3Mundos~3)~6      //     @name = name~6      //   end~6      //   def diHola~6      //     puts ~3Hola, #{@name}!~3~6      //   end~6      //   def diAdios~6      //     puts ~3Adios, #{@name}, hasta pronto!~3~6      //   end~6      // end~6      //~6      // g = Jello.new(~3Romuaseldo~3)~6      // g.diHola~6      // g.diAdios~6      //~6      // puts Jello.instance_methods~6      //~6~6#listix#~6~6   <-- bRun>~6      MSG, oConso clear~6      RUBY, @<xRubio>');INSERT INTO tLsxCmdAliases VALUES ('RUBY', 'RUBY');
