INSERT INTO tlsxCmdSyntaxParams VALUES ('GASTONA', '1', '1', 'parameter', ' ', 'first parameter of the program');
INSERT INTO tlsxCmdSyntaxParams VALUES ('GASTONA', '1', '2', '...', ' ', 'further parameters');INSERT INTO tlsxCmdSyntaxOpts VALUES ('GASTONA', '1', 'ON BATCH', '1 / 0', '0', 'If true (1) executes the command using a batch file (or script in linux). Note in Windows it might be necessary in some special cases due to a bug in the java function Runtime.getRuntime().exec (I guess)');
